INSERT INTO reviews (employer_id, author_name, rating, title, body, status, published_at, created_at, ip_address) VALUES
((SELECT id FROM employers WHERE slug = 'holtz-jacek-kaminski'), 'Anna K.', 5, 'Meble kuchenne idealne', 'Meble kuchenne wyszły idealnie. Wszystko zostało zaprojektowane zgodnie z moimi oczekiwaniami i zamontowane na czas.', 'published', '2021-05-12 10:00:00', '2021-05-12 10:00:00', '10.0.0.101'),
((SELECT id FROM employers WHERE slug = 'holtz-jacek-kaminski'), 'Marek Lewandowski', 5, 'Nowoczesne biuro w stylu loft', 'Świetna współpraca z zespołem projektowym. Pomogli nam stworzyć nowoczesne biuro w stylu industrialnym, a konstrukcje stalowe i szkło prezentują się fenomenalnie.', 'published', '2021-09-28 09:00:00', '2021-09-28 09:00:00', '10.0.0.102'),
((SELECT id FROM employers WHERE slug = 'holtz-jacek-kaminski'), 'Katarzyna P.', 5, 'Zabudowy wysokiej jakości', 'Zamówiliśmy zabudowę do salonu i sypialni. Widać dbałość o każdy detal i naprawdę wysoką jakość materiałów.', 'published', '2022-03-07 12:00:00', '2022-03-07 12:00:00', '10.0.0.103'),
((SELECT id FROM employers WHERE slug = 'holtz-jacek-kaminski'), 'Michał', 5, 'Lakierowane fronty jak w projekcie', 'Firma godna polecenia. Montaż przebiegł sprawnie, a fronty lakierowane wyglądają tak, jak w projekcie.', 'published', '2022-07-14 14:00:00', '2022-07-14 14:00:00', '10.0.0.104'),
((SELECT id FROM employers WHERE slug = 'holtz-jacek-kaminski'), 'Ewa i Piotr', 5, 'Przemyślana garderoba', 'Zleciliśmy wykonanie garderoby i jesteśmy bardzo zadowoleni. Każda półka, szuflada i drążek zostały przemyślane tak, aby maksymalnie wykorzystać przestrzeń.', 'published', '2022-11-02 16:00:00', '2022-11-02 16:00:00', '10.0.0.105'),
((SELECT id FROM employers WHERE slug = 'holtz-jacek-kaminski'), 'Radosław Z.', 5, 'Współpraca przy projektach', 'Profesjonalne podejście i świetny kontakt. Jako architekt wnętrz doceniam ich elastyczność i umiejętność dopasowania się do projektów.', 'published', '2023-02-18 11:00:00', '2023-02-18 11:00:00', '10.0.0.106'),
((SELECT id FROM employers WHERE slug = 'holtz-jacek-kaminski'), 'Julia', 5, 'Łazienka w kolorze NCS', 'Meble do łazienki w kolorze NCS wyszły pięknie. Polecam każdemu, kto chce mieć coś naprawdę wyjątkowego.', 'published', '2023-06-24 08:00:00', '2023-06-24 08:00:00', '10.0.0.107'),
((SELECT id FROM employers WHERE slug = 'holtz-jacek-kaminski'), 'Łukasz B.', 5, 'Stal nierdzewna i tempo', 'Świetna jakość stali nierdzewnej i szybka realizacja. Dzięki temu nasza restauracja zyskała nowoczesny wygląd.', 'published', '2023-09-09 10:30:00', '2023-09-09 10:30:00', '10.0.0.108'),
((SELECT id FROM employers WHERE slug = 'holtz-jacek-kaminski'), 'Patrycja', 5, 'Świetna obsługa i efekt', 'Bardzo miła obsługa, wszystko wyjaśnione krok po kroku. Jestem zachwycona efektem końcowym.', 'published', '2023-12-11 12:00:00', '2023-12-11 12:00:00', '10.0.0.109'),
((SELECT id FROM employers WHERE slug = 'holtz-jacek-kaminski'), 'Tomasz G.', 5, 'Recepcja wygląda ekskluzywnie', 'Duże doświadczenie i terminowość. Nasza recepcja w hotelu dzięki nim wygląda ekskluzywnie.', 'published', '2024-04-05 09:15:00', '2024-04-05 09:15:00', '10.0.0.110'),
((SELECT id FROM employers WHERE slug = 'holtz-jacek-kaminski'), 'Monika', 5, 'Montaż bez zarzutu', 'Panowie z montażu spisali się świetnie. Szybko, czysto i bez zbędnego zamieszania.', 'published', '2024-08-17 13:00:00', '2024-08-17 13:00:00', '10.0.0.111'),
((SELECT id FROM employers WHERE slug = 'holtz-jacek-kaminski'), 'Adam', 5, 'Witryny i stal do showroomu', 'Zamówiliśmy witryny szklane i konstrukcję stalową do showroomu. Efekt przerósł nasze oczekiwania.', 'published', '2025-02-19 12:25:00', '2025-02-19 12:25:00', '10.0.0.112'),
((SELECT id FROM employers WHERE slug = 'holtz-jacek-kaminski'), 'Renata P.', 5, 'Kuchnia – serce domu', 'Dzięki tej firmie nasza kuchnia stała się sercem domu. Kolory dobrane perfekcyjnie, a jakość wykończenia na najwyższym poziomie.', 'published', '2025-09-01 10:10:00', '2025-09-01 10:10:00', '10.0.0.113');
